import React from 'react';
import {Grid,Modal, Header, Button, Icon, Divider} from 'semantic-ui-react';
import '../Styles/App.css';

class OneBeer extends React.Component {

    state={openDetails: false}

    addToFavourites = () => {

        this.props.add(this.props.beer);

        this.setState({
            openDetails: false
        })
    }

render() {
    return (
        <>

        <Grid.Row columns='3' textAlign='center'>
            <Grid.Column>
                <p onClick={() => this.setState({openDetails: true})} className='beerColor detailsP'>{this.props.iterator}.{this.props.beer.name}</p>
            </Grid.Column>

            <Grid.Column>
                <p>{this.props.beer.tagline}</p>
            </Grid.Column>

             <Grid.Column>
                <p>{this.props.beer.first_brewed}</p>
            </Grid.Column>
        </Grid.Row>

        <Modal
        closeIcon
        open={this.state.openDetails}
        onClose={() => this.setState({openDetails: false})}
        onOpen={() => this.setState({openDetails: true})}
        >
        <Header textAlign='center' icon='beer' content='Details of your beer' />
        <Modal.Content>

            <Grid>
                <Grid.Row>
                    <Grid.Column textAlign='center'>
                    <Header className='beerColor up'>Description</Header>
                        <p className='black'>{this.props.beer.description}</p>
                    </Grid.Column>
                </Grid.Row>
            </Grid>

         <Divider></Divider>

        </Modal.Content>
      </Modal>

        </>
    )}

}

export default OneBeer;
