import React from 'react';
import {Item} from 'semantic-ui-react';
import '../Styles/App.css';

class OneBeer extends React.Component {

    componentDidMount = () => {

        if(this.props.beer.image_url == "") {
            this.props.beer.image_url= <Item.Image src={require('../Photos/beer.png')}></Item.Image>
        }
    }

render() {
    return (

         <Item className='border'>
             <Item.Image avatar src={this.props.beer.image_url} size='mini' />

             <Item.Content >
                 <Item.Header className='white'>
                     {this.props.beer.name}
                 </Item.Header>
             </Item.Content>
         </Item>

    )
}

}

export default OneBeer;
