import React from 'react';
import {Grid, Header, Segment, Divider, Button, Icon } from 'semantic-ui-react';
import '../Styles/App.css';
import axios from 'axios'
import OneBeer from './OneBeer'

class HomePage extends React.Component {

    state = {beers: [], iterator: 0, pagesUsed: []}

    componentDidMount = () => {

        // Pobierając 10 elementów mozemy wyszukiwac piwa na 33 stronach (5 z ostatniej)
        let page = Math.floor((Math.random() * 33) +1)
        let pages = this.state.pagesUsed;
        pages.push(page);

        axios.get('https://api.punkapi.com/v2/beers?per_page=10&page='+page)
        .then(response => {

            this.setState({
                beers:response.data,
                pagesUsed: pages
            })
        })

        .catch(error => {
            console.log(error)
        }) 
    }

    showMore = () => {

        let founded = false;

        do {
        //Pobranie kolejnych elementów z innej strony
        let newPage = Math.floor((Math.random() * 33) +1).toString();
        let pages = this.state.pagesUsed;

        if(!pages.some(page => page==newPage)) {

        founded = true;
        pages.push(newPage);

        axios.get('https://api.punkapi.com/v2/beers?per_page=10&page='+newPage)
        .then(response => {

            let beersNow = this.state.beers;
            let beersNew = response.data;
            let newBeers = beersNow.concat(beersNew);

            this.setState({
                beers: newBeers,
                iterator: 0,
                pagesUsed:pages
            })
        }) 
    }

    } while(founded!=true)

}

render() {
    return (

          <Segment className='width40 ml-1 transparent'>

              <Divider horizontal className='white mt-2 size-15'>Discover the bears from all over the world</Divider>

            <Grid className='mt-5'>

                <Grid.Row columns='4' textAlign='center'>
                    <Grid.Column widescreen='5'>
                        <Header className='up beerColor'>Name</Header>
                    </Grid.Column>

                    <Grid.Column width='5'>
                        <Header className='up beerColor'>Tagline</Header>
                    </Grid.Column>

                    <Grid.Column width='2'>
                        <Header className='up beerColor'>Alcohol</Header>
                    </Grid.Column>

                    <Grid.Column>
                        <Header className='up beerColor'><Icon name='heart'/> </Header>
                    </Grid.Column>
                </Grid.Row>

                <Divider className='mt-0'></Divider>


                {this.state.beers.map(beer => {
                    this.state.iterator++;
                    return <OneBeer add={this.props.add} iterator={this.state.iterator} beer={beer} />
                 })}

                 <Divider></Divider>

                 <Grid.Row>
                     <Grid.Column textAlign='center'>
                          <Header className='up white'>Click on name for more details and add to your favourites</Header>
                     </Grid.Column>
                 </Grid.Row>

                 <Grid.Row>
                     <Grid.Column textAlign='center' className='width40p centerMargin'>
                        <Button onClick={this.showMore} className='up buttonMore' color='yellow' basic circular fluid>Show more beers</Button>
                     </Grid.Column>
                 </Grid.Row>
            </Grid>    
          </Segment>
    )
}}

export default HomePage;
