import React from 'react';
import {Grid,Modal, Header, Button, Icon} from 'semantic-ui-react';
import '../Styles/App.css';

class OneBeer extends React.Component {

    state={openDetails: false}

render() {
    return (
        <>

        <Grid.Row columns='3' textAlign='center'>
            <Grid.Column>
                <p onClick={() => this.setState({openDetails: true})} className='beerColor detailsP'>{this.props.iterator}.{this.props.beer.name}</p>
            </Grid.Column>

            <Grid.Column>
                <p>{this.props.beer.tagline}</p>
            </Grid.Column>

             <Grid.Column>
                <p>{this.props.beer.first_brewed}</p>
            </Grid.Column>
        </Grid.Row>

        <Modal
        closeIcon
        open={this.state.openDetails}
        onClose={() => this.setState({openDetails: false})}
        onOpen={() => this.setState({openDetails: true})}
        >
        <Header icon='beer' content='Details of your beer' />
        <Modal.Content>
        <p className='beerColor'>{this.props.beer.name}</p>

        </Modal.Content>
        <Modal.Actions>
          <Button color='red' onClick={() => this.setState({openDetails: false})}>
            <Icon name='remove' /> No
          </Button>
          <Button color='green' onClick={() => this.setState({openDetails: false})}>
            <Icon name='checkmark' /> Yes
          </Button>
        </Modal.Actions>
      </Modal>

        </>
    )}

}

export default OneBeer;
