import React from 'react';
import {Grid, Header, Segment, Divider, Icon } from 'semantic-ui-react';
import '../Styles/App.css';
import axios from 'axios'
import OneBeer from './OneBeer'

class FavouritesPage extends React.Component {

    state = {iterator: 0}

    resetIterator = () => {
        this.setState({
            iterator:0
        })
    }

render() {
    return (

          <Segment className='width40 ml-1 transparent'>

              <Divider horizontal className='white mt-2 size-15'>Here are your favourites beers</Divider>

              <Grid className='mt-5'>

              {this.props.empty == true ? (

            <Grid.Row>
                <Grid.Column textAlign='center'>
                     <Header className='beerColor'>You didn't pick any favorite beer. Go to the home page and find your loved one!</Header>
                 </Grid.Column>
            </Grid.Row>


              ): (

                <>
                <Grid.Row columns='3' textAlign='center'>
                    <Grid.Column>
                        <Header className='up beerColor'>Name</Header>
                    </Grid.Column>

                    <Grid.Column>
                        <Header className='up beerColor'>Tagline | Fav</Header>
                    </Grid.Column>

                    <Grid.Column>
                        <Header className='up beerColor'>First brewed</Header>
                    </Grid.Column>
                </Grid.Row>

                <Divider className='mt-0'></Divider>

                {this.props.favouritesBeers.map(beer => {
                    this.state.iterator++;
                    return <OneBeer resetIterator={this.resetIterator} remove={this.props.remove} iterator={this.state.iterator} beer={beer} />
                 })}

                 </>
              )}

                 <Divider></Divider>  
                </Grid>  
          </Segment>
    )
}}

export default FavouritesPage;
