import React from 'react';
import {Grid,Modal} from 'semantic-ui-react';
import '../Styles/App.css';

class OneBeer extends React.Component {

    state={openDetails: false}

 showDetails = () => {
     console.log(this.props.beer.name)
 }

render() {
    return (
        <>

        <Grid.Row columns='3' textAlign='center'>
            <Grid.Column>
                <p onClick={this.showDetails} className='beerColor detailsP'>{this.props.iterator}.{this.props.beer.name}</p>
            </Grid.Column>

            <Grid.Column>
                <p>{this.props.beer.tagline}</p>
            </Grid.Column>

             <Grid.Column>
                <p>{this.props.beer.first_brewed}</p>
            </Grid.Column>
        </Grid.Row>

        <Modal
        open={this.state.openDetails}
        onClose={() => this.setState({openDetails: false})}
        onOpen={() => setOpen(true)}
      >
        <Header icon='archive' content='Archive Old Messages' />
        <Modal.Content>
          <p>
            Your inbox is getting full, would you like us to enable automatic
            archiving of old messages?
          </p>
        </Modal.Content>
        <Modal.Actions>
          <Button color='red' onClick={() => setOpen(false)}>
            <Icon name='remove' /> No
          </Button>
          <Button color='green' onClick={() => setOpen(false)}>
            <Icon name='checkmark' /> Yes
          </Button>
        </Modal.Actions>
      </Modal>

        </>
    )}

}

export default OneBeer;
