import React from 'react';
import {Grid} from 'semantic-ui-react';
import '../Styles/App.css';

class OneBeer extends React.Component {

 showDetails = () => {
     console.log(this.props.beer.name)
 }

render() {
    return (

        <Grid.Row columns='3' textAlign='center'>
            <Grid.Column>
                <p onClick={this.showDetails} className='beerColor detailsP'>{this.props.iterator}.{this.props.beer.name}</p>
            </Grid.Column>

            <Grid.Column>
                <p>{this.props.beer.tagline}</p>
            </Grid.Column>

             <Grid.Column>
                <p>{this.props.beer.first_brewed}</p>
            </Grid.Column>
        </Grid.Row>


    )
}

}

export default OneBeer;
