import React from 'react';
import {Grid, Header, Segment, Divider, Button } from 'semantic-ui-react';
import '../Styles/App.css';
import axios from 'axios'
import OneBeer from './OneBeer'

class HomePage extends React.Component {

    state = {beers: [], iterator: 0, currentPage: ''}

    componentDidMount = () => {

        // Pobierając 10 elementów mozemy wyszukiwac piwa na 33 stronach
        let page = Math.floor((Math.random() * 33) +1).toString();

        axios.get('https://api.punkapi.com/v2/beers?per_page=10&page='+page)
        .then(response => {

            this.setState({
                beers:response.data,
                currentPage: page
            })
        })

        .catch(error => {
            console.log(error)
        }) 
    }

    showMore = () => {

        //Pobranie kolejnych elementów z innej strony
        let page = Math.floor((Math.random() * 33) +1).toString();

            if(page != this.state.currentPage) {
                console.log(page)
            }
    }

render() {
    return (

          <Segment className='width40 ml-1 transparent'>

              <Divider horizontal className='white mt-2 size-15'>Discover the bears from all over the world</Divider>

            <Grid className='mt-5'>

                <Grid.Row columns='3' textAlign='center'>
                    <Grid.Column>
                        <Header className='up beerColor'>Name</Header>
                    </Grid.Column>

                    <Grid.Column>
                        <Header className='up beerColor'>Tagline</Header>
                    </Grid.Column>

                    <Grid.Column>
                        <Header className='up beerColor'>First brewed</Header>
                    </Grid.Column>
                </Grid.Row>

                <Divider className='mt-0'></Divider>


                {this.state.beers.map(beer => {
                    this.state.iterator++;
                    return <OneBeer iterator={this.state.iterator} beer={beer} />
                 })}

                 <Divider></Divider>

                 <Grid.Row>
                     <Grid.Column textAlign='center'>
                          <Header className='up white'>Click on name for more details or add the bear to your favourites</Header>
                     </Grid.Column>
                 </Grid.Row>

                 <Grid.Row>
                     <Grid.Column textAlign='center' className='width40p centerMargin'>
                        <Button onClick={this.showMore} className='up' color='yellow' basic fluid>Show more beers</Button>
                     </Grid.Column>
                 </Grid.Row>
            </Grid>    
          </Segment>
    )
}}

export default HomePage;
